import React from 'react';
import './App.css';
import Recipe from './Recipe';

class App extends React.Component {

  getRecipes() {
    fetch('http://localhost:8080/recipes')
      .then(response => response.json())
      .then(data => this.setState({ recipes: data }));
  }

  constructor(props) {
    super(props)
    this.state = {
      recipes: [
        {
          "name": "Oregano Marinated Chicken",
          "tags": [
            "main",
            "chicken"
          ],
          "ingredients": [
            "4 (6 to 7-ounce) boneless skinless chicken breasts\r",
            "10 grinds black pepper\r",
            "1/2 tsp salt\r",
            "2 tablespoon extra-virgin olive oil\r",
            "1 teaspoon dried oregano\r",
            "1 lemon, juiced"
          ],
          "instructions": []
        },
        {
          "name": "Green pea soup with cheddar scallion panini",
          "tags": [
            "soup",
            "main",
            "panini"
          ],
          "ingredients": [
            "4 (6 to 7-ounce) boneless skinless chicken breasts\r",
            "10 grinds black pepper\r",
            "1/2 tsp salt\r",
            "2 tablespoon extra-virgin olive oil\r",
            "1 teaspoon dried oregano\r",
            "1 lemon, juiced"
          ],
          "instructions": []
        }
      ]
    }
  }
  render() {
    return (<div>
      {this.state.recipes.map((recipe, index) => (
        <Recipe recipe={recipe} />))}
    </div>);
  }
}

export default App;
